// src/components/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import { database } from '../firebase';
import { List, ListItem, ListItemText, Button, Typography, Box } from '@mui/material';

const AdminDashboard = () => {
  const [characters, setCharacters] = useState([]);
  const [selectedCharacter, setSelectedCharacter] = useState(null);

  useEffect(() => {
    const fetchCharacters = async () => {
      const snapshot = await database.ref('characters').once('value');
      const charactersData = snapshot.val();
      setCharacters(Object.values(charactersData || {}));
    };

    fetchCharacters();
  }, []);

  const handleEditCharacter = (character) => {
    setSelectedCharacter(character);
  };

  const handleUpdateCharacter = (updatedCharacter) => {
    database.ref('characters').child(updatedCharacter.id).update(updatedCharacter);
    setSelectedCharacter(null);
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>Admin Dashboard</Typography>
      <Box>
        <Typography variant="h6">Characters List</Typography>
        <List>
          {characters.map((character) => (
            <ListItem key={character.id} button onClick={() => handleEditCharacter(character)}>
              <ListItemText primary={character.title} />
            </ListItem>
          ))}
        </List>
      </Box>
      {selectedCharacter && (
        <Box>
          <Typography variant="h6">Edit Character</Typography>
          {/* Character edit form */}
          <Button onClick={() => handleUpdateCharacter(selectedCharacter)} variant="contained" color="primary">
            Update Character
          </Button>
        </Box>
      )}
    </Box>
  );
};

export default AdminDashboard;
